function submitForm(){var name=$("#name").val(),email=$("#email").val(),msg_subject=$("#msg_subject").val(),message=$("#message").val();$.ajax({type:"POST",url:"assets/php/form-process.php",data:"name="+name+"&email="+email+"&msg_subject="+msg_subject+"&message="+message,success:function(text){"success"==text?formSuccess():(formError(),submitMSG(!1,text))}})}function formSuccess(){$("#contactForm")[0].reset(),submitMSG(!0,"Message Submitted!")}function formError(){$("#contactForm").removeClass().addClass("shake animated").one("webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",(function(){$(this).removeClass()}))}function submitMSG(valid,msg){if(valid)var msgClasses="h3 text-left tada animated text-success";else var msgClasses="h3 text-left text-danger";$("#msgSubmit").removeClass().addClass(msgClasses).text(msg)}$("#contactForm").validator().on("submit",(function(event){event.isDefaultPrevented()?(formError(),submitMSG(!1,"Did you fill in the form properly?")):(event.preventDefault(),submitForm())}));